import styled from 'styled-components';

export const HeaderWrapper = styled.div`
    display:flex;
    flex-direction:row;
    justify-content:space-between;
    align-items:center;
    position:fixed;
    width:100%;
    padding: 2rem 0;

    .logo1 {
        font-family: 'Anton', sans-serif;
        font-size:5rem;
        font-weight:600;
        letter-spacing: 0.2rem;
        line-height:100%
    }

    .logo2 {
        font-size:3rem;
    }

    .search {
        display:flex;
        flex-direction:row;
        align-items:center;
        justify-content:flex-end;
    }
`;
