import styled from "styled-components";

const media = {
  mobile: "@media(max-width:600px)",
};

const lightTheme = {
  navBg: "#ffffff",
  navColor: "#000000",
};

const darkTheme = {
  navBg: "#363636",
  navColor: "#ffffff",
};

export const NavbarWrap = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: ${(props) => (props.isDark ? darkTheme.navBg : lightTheme.navBg)};
  color: ${(props) => (props.isDark ? darkTheme.navColor : lightTheme.navColor)};

  .iconReact,
  .iconReactNav {
    padding: 2rem;
    font-size: 1.8rem;

    &:hover {
      cursor: pointer;
    }
  }
`;

export const Navbar2Wrap = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  background: ${(props) => (props.isDark ? darkTheme.navBg : lightTheme.navBg)};
  
  .iconReact,
  .iconReactDiv,
  .iconReactNav {
    padding: 2rem;
    font-size: 1.8rem;
    display: flex;
    flex-direction: row;
    align-items: center;

    &:hover {
      cursor: pointer;
    }
  }

  .navHome,
  .navTrend,
  .navLib,
  .navSetting {
    margin-left: 1.6rem;
    font-size: 1.3rem;
  }
`;
