import styled from "styled-components";

const lightTheme = {};

const darkTheme = {};

const media = {
  mobile: "@media(max-width:600px)",
};

export const LogModalWrap = styled.div`
  display: flex;
  z-index: 10;
  position: fixed;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  width: 100%;
  height: 100%;
  overflow: auto;
  background: rgba(0, 0, 0, 0.6);

  .container {
    background: #ffffff;
    width: 25%;
    padding: 2rem;
    margin: auto;
    border-radius: 5px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    &__para {
      font-size: 1.6rem;
      margin-bottom: 2rem;
    }

    &__btnDiv {
      display: flex;
      flex-direction: row;
      justify-content: center;
      align-items: center;
      width: 100%;

      &--btn1,
      &--btn2 {
        width: 30%;
        border: none;
        padding: 1re
      }
    }
  }
`;
