import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import { SignUpWrapper } from '../../styles/components/SignUpStyle';

export const SignUpForm = () => {
    const isDark = useSelector(state => state.dark.mode);
    const [name, handleName] = useState("");
    const [mobile, handleMobile] = useState("");
    const [email, handleEmail] = useState("");
    const [nameError, handleNameError] = useState(false);
    const [mobileError, handleMobileError] = useState(false);
    const [emailError, handleEmailError] = useState(false);
    const [buttonEnabled, handleButtonEnable] = useState(false);

    const setName = (e) => {
        handleName(e.target.value);
        if(e.target.value.length > 3) {
            handleNameError(false);
        }
    }

    const checkNameError = (e) => {
        if(e.target.value.length < 3) {
            handleNameError(true);
        }else{
            handleNameError(false);
        }
    }

    const setMobile = (e) => {
        if(e.target.value.length <= 10){
            handleMobile(e.target.value)
        }
    }

    const checkMobileError = (e) => {
        if(e.target.value.length < 10) {
            handleMobileError(true);
        }else{
            handleMobileError(false);
        }
    }

    const setEmail = (e) => {
        handleEmail(e.target.value)
    }

    const validation = () => {
        if(name !== "" && mobile !== "" && email !== "" && !nameError && !mobile && !email) {
            hnl
        }
    }
    
    return (
        <SignUpWrapper isDark={isDark}>
            <div className="formHeader">Register to buy books</div>
            <input className="formInput" value={name} onChange={setName} onBlur={checkNameError} placeholder="eg. Rakshith Acharya" />
            {nameError ? <span className="formError">*Name not valid</span> : ""}
            <input className="formInput" value={mobile} onChange={setMobile} placeholder="eg. 9999999999" type="number" />
            {mobileError ? <span className="formError">*Mobile number not valid</span> : ""}
            <input className="formInput" value={email} onChange={setEmail} placeholder="eg. example.company.com" />
            {emailError ? <span className="formError">*Email address not valid</span> : ""}
            <button disabled={!buttonEnabled} className="formButton">Submit</button>
        </SignUpWrapper>
    )
}
