    display: flex;
    flex-direction: column;

    .formInput {
        padding: 2rem;
        border: 1px solid #e5e5e5;
        border-radius: 5px;
        margin-bottom: 1.8rem;
    }

    .formButton {
        padding: 2rem;
        border: 1px solid #e5e5e5;
