import styled from 'styled-components';

export const LoginWrapper = styled.div`
padding-top2.3rem;
    padding: 2rem;
    background: #ffffff;
    border-radius: 5px;
    display: flex;
    flex-direction: column;

    .formHeader {
        font-family: 'Anton',sans-serif;
        font-size: 3rem;
        margin-bottom: 2.4rem;
    }

    .formInput {
        padding: 2rem;
        border: 1px solid #e5e5e5;
        border-radius: 5px;
        margin-bottom: 1.8rem;
    }

    .formInput:focus {
        outline:none;
        border: 1px solid #383838;
    }

    .formButton {
        padding: 1.8rem;
        border: 1px solid #e5e5e5;
        border-radius: 6px;
        background: #383838;
        font-size:2.2rem;
        font-weight: 600;
        color: #ffffff;
    }
`;
