import styled from 'styled-components';

const lightTheme = {
    popularBg:"#f0912b",
    quoteColor:"#ffffff",
    quoteShadow:"#b50000",
}

export const PopularWrapper = styled.div`
    background: ${lightTheme.popularBg};
    display:flex;
    flex-direction:row;

    .popularCol1 {
        width: 40%;
    }

    .bookImg {
        width: 100%;
    }

    .popularCol2 {
        width: 60%;
        padding: 10rem;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
    }

    .quote1 {
        margin-top: 4rem;
        color: ${lightTheme.quoteColor};
        font-size: 4rem;
        line-height: 140%;
        text-shadow: 3px 3px ;
        text-align: center;
        font-family: 'Abril Fatface', cursive;
    }
`;
