import styled from 'styled-components';

const lightTheme = {
    featureBg:"#ffffff",
    featureHeadColor:"#f0912b",
    featureSubHeadColor:"#b50000",
    featureSubDes:"#000000",
}

const darkTheme = {
    featureBg:"#000000",
    featureHeadColor:"#0bbdab",
    featureSubHeadColor:"#757575",
    featureSubDes:"#ffffff",
}

const media = {
    mobile:'@media(max-width:600px)'
}

export const FeatureWrapper = styled.div`
    background: ${props => props.isDark ? darkTheme.featureBg : lightTheme.featureBg};
    padding:6rem 10rem;
    padding-bottom: 10rem;

    .featureHeader{
        text-align:center;
        font-family: 'Anton',sans-serif;
        font-size: 4rem;
        margin-bottom: 6rem;
        color: ${props => props.isDark ? darkTheme.featureHeadColor : lightTheme.featureHeadColor};
    }

    .featureBody{
        display:grid;
        grid-template-columns: 1fr 1fr 1fr;
        grid-gap: 10rem;
        width: 100%;

        &__container {
            display:flex;
            flex-direction:column;
            justify-content:flex-start;
            align-items: center;
            text-align:center;

            &--image {
                margin-bottom: 2rem;
            }

            &--heading {
                margin-bottom: 0.8rem;
                font-size: 2rem;
                font-family: 'Abril Fatface',cursive;
                color: ${props => props.isDark ? darkTheme.featureSubHeadColor : lightTheme.featureSubHeadColor};
            }

            &--description {
                color: ${props => props.isDark ? darkTheme.featureSub : lightTheme.featureSub};
            }
        }
    }
`;
