import styled from 'styled-components';

export const HeaderWrapper = styled.div`
    display:flex;
    flex-direction:row;
    justify-content:space-between;
    align-items:center;
    position:fixed;
    width:100%;
    padding: 2rem 0;

    .logo1 {
        font-family: 'Anton', sans-serif;
        font-size:5rem;
        font-weight:600;
        letter-spacing: 0.2rem;
        line-height:100%;
        margin-left: 4rem;
        color: #f0912b;
        margin-top: 1rem;
    }

    .logo2 {
        font-size:3rem;
        color: #000000;
        color: #383838;
    }

    .search {
        display:flex;
        flex-direction:row;
        align-items:center;
        justify-content:flex-end;
        margin-right: 4rem;
    }

    .searchBar {
        background: transparent;
        border: none;
        border-bottom: 1px solid #000000;
        width: 100%;
        padding: 0 2rem;
        padding-bottom: 0.8rem;
        border-bottom: 1px solid #000000;
        margin-right: 1.5rem;
    }

    .searchBar:focus {
        outline:none
    }

    .searchImg {
        width: 20px;
        height: 20px
    }
`;
