import React, { useState } from 'react';
import blackSearch from "../images/blackSearch.png";
import { HeaderWrapper } from '../styles/components/HeaderStyle';

export const Header = () => {
    const [showSearch, setShowSearch] = useState(false);

    onSearchClick = () => {
        if(showSearch){
            setShowSearch(false);
        }else{
            setShowSearch(true)
        }
    }

    return (
        <HeaderWrapper>
            <div className="logo1">BOOK<span className="logo2">shelf</span></div>
            <div className="search">
                {showSearch?<input className="searchBar" placeholder="search . . ." />:""}
                <img className="searchImg" onClick={ons} src={blackSearch} alt="blackSearch" />
            </div>
        </HeaderWrapper>
    )
}
