import styled from 'styled-components';

const lightTheme = {
    bannerBg:"#ffffff",
    para1:"#383838",
    para2:"#f0912b",
} 

export const BannerWarapper = styled.div`
    margin-top:10.3rem;
    display:flex;
    flex-direction:row;
    padding: 10rem 0;
    background: ${lightTheme.bannerBg};
    height: 100vh;

    .bannerCol1 {
        width: 70%;
        padding: 10rem;
        font-family: 'Yusei Magic', sans-serif;
    }

    .para1 {
        font-size: 3rem;
        line-height: 150%;
        color:${lightTheme.para1};
    }

    .para2 {
        font-size: 4rem;
        line-height: 130%;
        font-family: 'Anton', sans-serif;
        color:${lightTheme.para2};
    }

    .bannerCol2 {
        width: 30%;
    }

    .bannerImg {
        width: 100%;
    }
`;
