import styled from "styled-components";

const media = {
  mobile: "@media(max-width:600px)",
};

const lightTheme = {
  bannerBg: "#f0912b",
  para1: "#383838",
  para2: "#f0912b",
  conHead: "#000000"
};

const darkTheme = {
  bannerBg: "#000000",
  para1: "#757575",
  para2: "#0bbdab",
  conHead: "#000000"
};

export const DashContentWrap = styled.div`
  background: ${(props) =>
    props.isDark ? darkTheme.bannerBg : lightTheme.bannerBg};

  .contentHeader {
    padding-left: 2rem;
    margin-top: 10rem;
    font-size: 3rem;
    font-weight: 700;
    line-height: 120%;
  }
`;
