import styled from 'styled-components';

const lightTheme = {
    bannerBg:"#ffffff",
    para2:"#f0912b",
    para1:"#383838",
} 

export const BannerWarapper = styled.div`
    margin-top:10.3rem;
    display:flex;
    flex-direction:row;
    padding-top: 10rem;
    background: ${lightTheme.bannerBg};

    .bannerCol1 {
        width: 70%;
        padding: 10rem;
    }

    .para1 {
        font-size: 3rem;
        line-height: 150%;
    }

    .para2 {
        font-size: 4rem;
        line-height: 130%;
    }

    .bannerCol2 {
        width: 30%;
    }

    .bannerImg {
        width: 100%;
    }
`;
