import styled from 'styled-components';

const lightTheme = {
    footerBg:"#051c38",
    footerCol1Color:"#ffffff",
    footerlLabelColor:"#388fd1",
}

const darkTheme = {
    footerBg:"#0bbdab",
    footerCol1Color:"#000000",
    footerlLabelColor:"#388fd1",
}

export const FooterWrapper = styled.div`
    padding:6rem;
    width: 100%;
    display:flex;
    flex-direction:row;
    align-items:center;
    background: ${ props => props.isDark ? darkTheme.footerBg : lightTheme.footerBg};  

    .footerCol1 {
        width: 40%;
        font-family: 'Abril Fatface',cursive;
        color: ${ props => props.isDark ? darkTheme.footerCol1Color : lightTheme.footerCol1Color}; 

        &__createdLabel{
            font-size: 4rem;
            line-height: 150%;
        }

        &__createdName {
            font-size: 4rem;
            line-height: 150%;
        }
    }

    .footerCol2 {
        width: 60%;
        display:flex;
        flex-direction:row;
        align-items: center;
        justify-content: space-between;

        &__content{
            width: 50%;

            &--row1, &--row2{
                display:flex;
                flex-direction:row;
                align-items:flex-start;
                font-size: 1.8rem;
                line-height: 180%;
                color: ${ props => props.isDark ? darkTheme.footerlLabelColor : lightTheme.footerlLabelColor}; 

                &__label {
                    width:30%;
                    font-weight:600;
                }
                &__colan {
                    width:10%;
                    font-weight:600;
                }
                &__number {
                    width:60%;
                }
            }
        }
    }
`;
