import styled from "styled-components";

const media = {
  mobile: "@media(max-width:600px)",
};

const lightTheme = {};

const darkTheme = {};

export const DashPopularWrap = styled.div`
  width: 70%;
  font-size: 1.3rem;
  .bookCover {
    width: 100%;
  }
  .bookName {
    margin-top: 1rem;
  }
`;
