import styled from "styled-components";

const media = {
  mobile: "@media(max-width:600px)",
};

const lightTheme = {
  bannerBg: "#f0912b",
  para1: "#383838",
  para2: "#f0912b",
};

const darkTheme = {
  bannerBg: "#000000",
  para1: "#757575",
  para2: "#0bbdab",
};

export const DashboardWrap = styled.div`
  display: flex;
  flex-direction: row;

  .navbar {
    width: 10%;
  }
  .dashboard {
    width: 90%;
    background:${lightTheme}
  }
`;
