import styled from "styled-components";

const media = {
  mobile: "@media(max-width:600px)",
};

const lightTheme = {
  toggleColorBefore: "#000000",
  toggleColorAfter: "#757575",
  toggleLabelColor: "#383838",
};

const darkTheme = {
  toggleColorBefore: "#383838",
  toggleColorAfter: "#cccccc",
  toggleLabelColor: "#ffffff",
};

export const DashHeadWrap = styled.div`
  .darkMode {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-right: 1.5rem;

    &__para {
      font-size: 1rem;
      text-align: center;
      color: ${(props) =>
        props.isDark
          ? darkTheme.toggleLabelColor
          : lightTheme.toggleLabelColor};
    }
  }

  .switch {
    display: inline-block;
    height: 14px;
    position: relative;
    width: 40px;
    margin-bottom: 0.5rem;
  }

  .switch input {
    display: none;
  }

  .slider {
    background-color: ${lightTheme.toggleColorBefore};
    bottom: 0;
    cursor: pointer;
    left: 0;
    position: absolute;
    right: 0;
    top: 0;
    transition: 0.4s;
  }

  .slider:before {
    background-color: #fff;
    bottom: 4px;
    content: "";
    height: 6px;
    left: 4px;
    position: absolute;
    transition: 0.4s;
    width: 6px;
  }

  input:checked + .slider {
    background-color: ${lightTheme.toggleColorAfter};
  }

  input:checked + .slider:before {
    transform: translateX(26px);
  }

  .slider.round {
    border-radius: 34px;
  }

  .slider.round:before {
    border-radius: 50%;
  }
`;
