import styled from 'styled-components';

const lightTheme = {
    featureBg:"#ffffff",
}

const darkTheme = {
    featureBg:"#000000",
}

const media = {
    mobile:'@media(max-width:600px)'
}

export const FeatureWrapper = styled.div`
    background: ${props => props.isDark ? darkTheme.featureBg : lightTheme.featureBg};
    padding:6rem 4rem;

    .featureHeader{
        text-align:center;
    }
`;
